#include <Wire.h>

void setup() {
  // Start the first I2C bus as master
  Wire.begin();
  Wire.setClock(400000);

  // Start the second I2C bus as slave on address 8
  Wire1.begin(8);
  Wire1.setClock(400000);
  Wire1.onReceive(receiveEvent);

  pinMode(LED_BUILTIN, OUTPUT);
}

void receiveEvent(int Nbytes) {
  while (Wire1.available()) {
    digitalWrite(LED_BUILTIN, Wire1.read());
  }
}

void loop() {
  Wire.beginTransmission(8);
  Wire.write(255);
  Wire.endTransmission();
  delay(500);
  Wire.beginTransmission(8);
  Wire.write(0);
  Wire.endTransmission();
  delay(500);
}

#include <Wire.h>

// constants
const int dt = 2000; // sampling rate [microseconds]
const int N = 250;   // buffer length
const int Nch = 12;  // number of channels
const int L = 200;   // boxcar length

unsigned long sum[Nch] = {0};

void setup() {
  unsigned short add_ptr = 0;
  unsigned short sub_ptr = add_ptr + N - L;
  unsigned long old_micros = 0, new_micros = 0;
  unsigned int bufftime[N] = {0};
  unsigned short buffdata[N][Nch] = {0};
  unsigned short old_voltage = 0, new_voltage = 0;
//  unsigned long sum[Nch] = {0};
  unsigned short i = 0, imax = N+749;

  Serial.begin(250000);
  analogReadResolution(12);
  analogWriteResolution(12);
  for (byte i=54; i<66; i++) {
    pinMode(i, INPUT);
  }

  // Start the first I2C bus as master
  Wire.begin();
  Wire.setClock(400000);

  // begin I2C bus on address 9
  Wire1.begin(9);
  Wire1.setClock(400000);
  Wire1.onReceive(receiveEvent);

  // take samples every dt microseconds
//  while (true) {
  while (i<imax) {
    new_micros = micros();
    if ((new_micros - old_micros) >= dt) {
      for (byte j=0; j<Nch; j++) {
        new_voltage = analogRead(54+j);
        old_voltage = buffdata[sub_ptr][j];
        buffdata[add_ptr][j] = new_voltage;
        sum[j] += new_voltage - old_voltage;
        if (j==0) {
          Wire.beginTransmission(9);
          for (byte i=0; i<3; i++) {
            Wire.write(sum[j] >> (8*i));
          }
          Wire.endTransmission();
        }
      }

      bufftime[add_ptr] = new_micros;
      old_micros = new_micros;
      (add_ptr==N-1) ? add_ptr = 0 : add_ptr++;
      (sub_ptr==N-1) ? sub_ptr = 0 : sub_ptr++;

      i++;
    }
  }

//  // read output
////  Serial.println();
//  for (int i=0; i<N; i++) {
//    Serial.print(bufftime[i]);
//    for (byte j=0; j<Nch-1; j++) {
//      Serial.print(" ");
//      Serial.print(buffdata[i][j]);
//    }
//    Serial.print(" ");
//    Serial.println(buffdata[i][Nch-1]);
//  }
//
//  Serial.println();
//  for (int j=0; j<Nch; j++) {
//    Serial.print(sum[j]);
//    Serial.print(" ");
//  }
//  Serial.println();

}

void receiveEvent(int Nbytes) {
  unsigned int x = 0;
  while (Wire1.available()) {
    for (byte i=0; i<Nbytes; i++) {
      x = (Wire1.read() << (8*i)) | x;
    }
  }
}

void loop() {
  // empty because the loop is in the setup()
}
